import { createContext } from "react";

export const sounds = {
  byKeyCode: {
    //  Q, W, E, A, S, D, Z, X, C.
    65: { letter: "A", order: 4 },
    67: { letter: "C", order: 9 },
    68: { letter: "D", order: 6 },
    69: { letter: "E", order: 3 },
    81: { letter: "Q", order: 1 },
    83: { letter: "S", order: 5 },
    87: { letter: "W", order: 2 },
    88: { letter: "X", order: 8 },
    90: { letter: "Z", order: 7 },
  },
};

export const SoundsContext = createContext(sounds);
