import React, { useCallback, useEffect, useRef } from "react";

import "./styles.scss";

export const SoundButton = ({
  keyCode,
  letter,
  soundSource,
  onPlay,
  isActive,
}) => {
  const audioTag = useRef(null);

  const onSoundButtonClick = useCallback(() => {
    audioTag.current.play();
    onPlay(keyCode);
  }, [keyCode, onPlay]);

  const onKeyPress = useCallback(
    (ev) => {
      const eventKeyCode = parseInt(ev.key.toUpperCase().charCodeAt(0));
      console.log(eventKeyCode, keyCode, ev);
      if (audioTag.current != null && keyCode === eventKeyCode) {
        audioTag.current.play();
        onPlay(keyCode);
      }
    },
    [keyCode, onPlay]
  );

  useEffect(() => {
    if (soundSource != null) {
      audioTag.current.src = soundSource;
    }
  }, [soundSource]);

  useEffect(() => {
    document.addEventListener("keyup", onKeyPress);

    return () => {
      document.removeEventListener("keyup", onKeyPress);
    };
  }, [onKeyPress]);

  const buttonClasses = ["drum-pad", isActive && "active"].join(" ");

  return (
    <button onClick={onSoundButtonClick} id={keyCode} className={buttonClasses}>
      <audio
        ref={audioTag}
        style={{ display: "none" }}
        className={"clip"}
        id={letter}
      />
      {letter}
    </button>
  );
};
